<?phpabstract class GenericPage{	var $js = array();	var $css = array();	// DYNAMIC DATA	var $locale;	var $lang;	var $user;	var $query;	var $path = '';	var $tab = -1;	var $contentTypeId = 0;	var $pageTypeId = 0;	var $title = '';	var $full_title = '';	var $icon = '';	var $finalized;	/****************** METHODS ******************/		// Constructor	public function __construct()	{		$this->SetTitle('');	}	// TITLE	public function SetTitle($title)	{		$this->title = $title;		$this->full_title = (empty($title) ? '' : $title.' - ').WEBSITE_NAME;		$this->initDynamicData();	}	// DISPLAYERS	abstract public function finalize();	public function display($name)	{		$this->finalized = true;		include('phtml/'.$name.'.php');	}	public function brick($name)	{		include('phtml/bricks/'.$name);	}	// CLIENT FILES	public function addJS($name, $unshift = false)	{		if(!in_array($name, $this->js))		{			if($unshift)				array_unshift($this->js, $name);			else				$this->js[] = $name;		}	}	public function addCSS($name, $unshift = false)	{		if(!in_array($name, $this->css))		{			if($unshift)				array_unshift($this->css, $name);			else				$this->css[] = $name;		}	}	// ETC	public function isCommentable() { return false; }	private function initDynamicData()	{		$this->locale = Main::$locale;		$this->lang = Main::$lang;		if(Main::$user)			$this->user = &Main::$user;		$this->query = $_SERVER['QUERY_STRING'];		$this->addJS(CLIENTFILE_JQUERY_JS);		$this->addJS(constant('CLIENTFILE_LOCALE_'.$this->locale.'_JS'));		$this->addJS(CLIENTFILE_BASIC_JS);		$this->addJS(CLIENTFILE_GLOBAL_JS);		$this->addCSS(CLIENTFILE_BASIC_CSS);		$this->addCSS(CLIENTFILE_GLOBAL_CSS);		$this->addCSS(constant('CLIENTFILE_LOCALE_'.$this->locale.'_CSS'));	}	public function __wakeup()	{		$this->initDynamicData();	}}?>